import React, { useState } from "react";
import { BrowserRouter as Router, Route, Switch, Link, Routes } from 'react-router-dom';
import User from "./user.js";
import "./App.css";
import { useHistory } from 'react-router-dom';

function App() {
  const [user, setUser] = useState(null);
  const [inputValue, setInputValue] = useState('');
  const handleChange = (event) => {
    setInputValue(event.target.value);
  };
  const handleSubmit = (event) => {
    event.preventDefault();

    // Send the input value to the backend
    sendInputToBackend(inputValue);

    // Reset the input value
    setInputValue('');
    history.push(`/user/${region}/${inputValue}`);
  };

  const sendInputToBackend = (value) => {
    fetch('http://localhost:8000/getUser', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({ input: value })
    })
    .then((res) => res.json())
    .then((data) => setUser(data.user))
    .catch(error => {
      console.error('Error:', error);
    });
  };


  return (

    <div className="App">
      <Router>
      <form onSubmit={handleSubmit}>
        <input type="text" value={inputValue} onChange={handleChange} />
        <button type="submit">Send</button>
      </form>
        <Routes>
          <Route path="/user/:region/:input" Component={User}></Route>
        </Routes>
    </Router>
    </div>
  );
}

export default App
