import React, { useState, useEffect } from "react";
import "./App.css";

function App() {
  const [message, setMessage] = useState("");
  const [inputValue, setInputValue] = useState('');
  const handleChange = (event) => {
    setInputValue(event.target.value);
  };
  const handleSubmit = (event) => {
    event.preventDefault();

    // Send the input value to the backend
    sendInputToBackend(inputValue);

    // Reset the input value
    setInputValue('');
  };

  const sendInputToBackend = (value) => {
    // Perform the backend API call here using your preferred method (fetch, axios, etc.)
    // You can use the 'value' parameter to access the input value and send it to the backend
    // Example:
    fetch('/your-backend-endpoint', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({ input: value })
    })
    .then((res) => res.json())
    .then((data) => setMessage(data.message))
    .catch(error => {
      console.error('Error:', error);
    });
  };

  useEffect(() => {
    fetch("http://localhost:8000")
      .then((res) => res.json())
      .then((data) => setMessage(data.message));
  }, []);

  return (
    <div className="App">
      <h1>{message}</h1>
    </div>
  );
}

export default App
