import React, { useState } from 'react';

const Match = ({ match }) => {
  const [expanded, setExpanded] = useState(false);

  const toggleExpanded = () => {
    setExpanded(!expanded);
  };
  useEffect(() => {
    // Fetch user's matches
    const fetchMatch = async () => {
      try {
        const response = await fetch(`http://localhost:8000/getMatchInfo`, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ input: match }),
        });
        const data = await response.json();
        console.log("data is here "+ data);
        setMatches(data.message);
      } catch (error) {
        console.error('Error fetching user matches:', error);
      }
    };

    if (match) {
      fetchMatch();
    }
  }, [match]);

  return (
    <div className="match">
      <div className="match-summary" onClick={toggleExpanded}>
        <h3>Match ID: {match.matchId}</h3>
        <p>Game Mode: {match.info.gameMode}</p>
        <p>Duration: {match.info.gameDuration}</p>
        {/* Display other basic match information */}
      </div>
      {expanded && (
        <div className="match-details">
          {/* Display additional match details */}
        </div>
      )}
    </div>
  );
};

export default Match;
