import React, { useState } from "react";
import { BrowserRouter as Router, Route, Routes, Link, useParams, useNavigate } from 'react-router-dom';
import User from "./user.js";
import "./App.css";

function App() {
  const [user, setUser] = useState(null);
  const [inputValue, setInputValue] = useState('');
  const [selectedRegion, setSelectedRegion] = useState('na1');
  const navigate = useNavigate();

  const handleChange = (event) => {
    setInputValue(event.target.value);
  };

  const handleRegionChange = (event) => {
    setSelectedRegion(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    // Send the input value to the backend
    sendInputToBackend(inputValue);

    // Reset the input value
    setInputValue('');
    navigate.push(`/user/${selectedRegion}/${inputValue}`);
  };

  const sendInputToBackend = (value) => {
    fetch('http://localhost:8000/getUser', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({ input: value })
    })
    .then((res) => res.json())
    .then((data) => setUser(data.user))
    .catch(error => {
      console.error('Error:', error);
    });
  };


  return (
    <Router>
    <div className="App">

      <form onSubmit={handleSubmit}>
      <label>
          Region:
          <select value={selectedRegion} onChange={handleRegionChange}>
            <option value="na1">NA</option>
            <option value="euw1">EUW</option>
            <option value="eun1">EUNE</option>
            {/* Add more regions as needed */}
          </select>
        </label>
        <input type="text" value={inputValue} onChange={handleChange} />
        <button type="submit">Send</button>
      </form>
      <Routes>
          <Route path="/user/:region/:input" element={<User />} />
        </Routes>


    </div>
    </Router>
  );
}

export default App
