import React, { useEffect, useState } from 'react';
import MatchList from './matchList.js';
import { useParams } from 'react-router-dom';

const UserPage = ({ }) => {
  const {region, input} = useParams();
  const [userInfo, setUserInfo] = useState(null);
  const [matches, setMatches] = useState([]);
  const [matchInfoList, setMatchInfoList] = useState([]);

  useEffect(() => {
    const fetchMatchesAndMatchInfo = async () => {
      try {
        const response = await fetch(`http://localhost:8000/getMatches`, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ input: userId }),
        });
  
        if (!response.ok) {
          throw new Error('Failed to fetch user matches');
        }
  
        const data = await response.json();
        setMatches(data.message);
  
        // Fetch match info after matches have been fetched
        const matchInfoData = await Promise.all(
          data.message.map(async (match) => {
            const matchResponse = await fetch(`http://localhost:8000/getMatchInfo`, {
              method: 'POST',
              headers: {
                'Content-Type': 'application/json',
              },
              body: JSON.stringify({ input: match }),
            });
  
            if (!matchResponse.ok) {
              throw new Error(`Failed to fetch match info for matchId: ${match}`);
            }
  
            const matchInfo = await matchResponse.json();
            return matchInfo.message;
          })
        );
  
        setMatchInfoList(matchInfoData);
      } catch (error) {
        console.error('Error fetching user matches:', error);
      }
    };
  
    if (userId) {
      fetchMatchesAndMatchInfo();
    }
  }, [userId]);

  return (
    <div>
      <p>{JSON.stringify(userId)}</p>
        <div>
          <MatchList userId = {userId} matches={matchInfoList} />
        </div>
    </div>
  );
};

export default UserPage;
