import React, { useState } from 'react';
import Participants from './participants.js';

const Match = ({ userId, match }) => {
  const [expanded, setExpanded] = useState(false);
  console.log(match);
  const playerIndex = match.metadata.participants.indexOf(userId);
  const player = match.info.participants[playerIndex];
  console.log(player);
  const toggleExpanded = () => {
    setExpanded(!expanded);
  };

  return (
    <div style={{backgroundColor: player.win ? 'green' : 'red'}}className="match">
      <div className="match-summary" onClick={toggleExpanded}>
        <h3>Match ID: {match.metadata.matchId}</h3>
        <h4>User ID: {userId}</h4>
        <h4>Champion: {player.championName}</h4>
        {/* Display other basic match information */}
      </div>
      {expanded && (
        <div className="match-details">
            <h4>Damage dealth: {player.magicDamageDealtToChampions + player.physicalDamageDealtToChampions}</h4>
            <Participants participants={match.info.participants}/>
          {/* Display additional match details */}
        </div>
      )}
    </div>
  );
};

export default Match;
