import React, { useState } from "react";
import { BrowserRouter as Router, Route, Switch, Link, Routes } from 'react-router-dom';
import User from "./user.js";
import "./App.css";
import { useNavigate } from 'react-router-dom';

function App() {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [inputValue, setInputValue] = useState('');
  const [selectedRegion, setSelectedRegion] = useState('na1');

  const handleChange = (event) => {
    setInputValue(event.target.value);
  };

  const handleRegionChange = (event) => {
    setSelectedRegion(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    // Send the input value to the backend
    sendInputToBackend(inputValue);

    // Reset the input value
    setInputValue('');
    navigate.push(`/user/${selectedRegion}/${inputValue}`);
  };

  const sendInputToBackend = (value) => {
    fetch('http://localhost:8000/getUser', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({ input: value })
    })
    .then((res) => res.json())
    .then((data) => setUser(data.user))
    .catch(error => {
      console.error('Error:', error);
    });
  };


  return (

    <div className="App">

    </div>
  );
}

export default App
