import React, { useEffect, useState } from 'react';
import MatchList from './matchList.js';
import Rank from './rank.js';
import { useParams } from 'react-router-dom';

const UserPage = () => {
  const { region, userName } = useParams();

  const [user, setUser] = useState(null);
  const [userId, setUserId] = useState(null);
  const [matches, setMatches] = useState([]);
  const [matchInfoList, setMatchInfoList] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchUserAndMatches = async () => {
      try {
        setLoading(true);
        const response = await fetch(`http://localhost:8000/getUser`, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ region, userName }), // Use destructuring here
        });

        if (!response.ok) {
          throw new Error('Failed to fetch user');
        }

        const data = await response.json();
        setUserId(data.user.puuid);
        console.log(userId);
        setUser(JSON.stringify(data.user));

        // Fetch matches only if the user data is available
        if (userId) {
          const matchesResponse = await fetch(`http://localhost:8000/getMatches`, {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
            },
            body: JSON.stringify({ input: userId }),
          });

          if (!matchesResponse.ok) {
            throw new Error('Failed to fetch user matches');
          }

          const matchesData = await matchesResponse.json();
          setMatches(matchesData.message);

          // Fetch match info after matches have been fetched
          const matchInfoData = await Promise.all(
            matchesData.message.map(async (match) => {
              const matchResponse = await fetch(`http://localhost:8000/getMatchInfo`, {
                method: 'POST',
                headers: {
                  'Content-Type': 'application/json',
                },
                body: JSON.stringify({ input: match }),
              });

              if (!matchResponse.ok) {
                throw new Error(`Failed to fetch match info for matchId: ${match}`);
              }

              const matchInfo = await matchResponse.json();
              return matchInfo.message;
            })
          );

          setMatchInfoList(matchInfoData);
          setLoading(false);
        }
      } catch (error) {
        console.error('Error fetching user data and matches:', error);
        setLoading(false);
      }
    };

    fetchUserAndMatches();
  }, [region, userName]); // Add region and userName as dependencies here

  return (
    <div>
      {loading ? ( // Display a loading indicator when the data is being fetched
        <p>Loading...</p>
      ) : (
        <div>
          <div>
            <p>{user}</p>
            <MatchList userId={userId} matches={matchInfoList} />
            <Rank summonerId={user.id} region={region} />
          </div>
        </div>
      )}
    </div>
  );
};

export default UserPage;
